// Calculator functions
const add = (a, b) => a + b;
const subtract = (a, b) => a - b;
const multiply = (a, b) => a * b;
const divide = (a, b) => a / b;

const calculate = (a, b, operation) => {
  switch(operation) {
    case 'add':
      return add(a, b);
    case 'subtract':
      return subtract(a, b);
    case 'multiply':
      return multiply(a, b);
    case 'divide':
      return divide(a, b);
    default:
      return 'Invalid operation';
  }
};

// Test your functions
console.log('Add:', add(10, 5)); // 15
console.log('Subtract:', subtract(10, 5)); // 5
console.log('Multiply:', multiply(10, 5)); // 50
console.log('Divide:', divide(10, 5)); // 2
console.log('Calculate add:', calculate(10, 5, 'add')); // 15
console.log('Calculate multiply:', calculate(10, 5, 'multiply')); // 50

