exercises.solution:
const students = [
  {
    name: 'Alice',
    age: 20,
    grade: 'A',
    subjects: ['Math', 'Science']
  },
  {
    name: 'Bob',
    age: 22,
    grade: 'B',
    subjects: ['History', 'English']
  },
  {
    name: 'Charlie',
    age: 21,
    grade: 'A',
    subjects: ['Math', 'Physics']
  }
];

const getStudentNames = () => {
  return students.map(student => student.name);
};

const getAverageAge = () => {
  const totalAge = students.reduce((sum, student) => sum + student.age, 0);
  return totalAge / students.length;
};

const getStudentsByGrade = (grade) => {
  return students.filter(student => student.grade === grade);
};

const addSubjectToStudent = (studentName, subject) => {
  const student = students.find(s => s.name === studentName);
  if (student) {
    student.subjects.push(subject);
  }
};

// Test functions
console.log('Student names:', getStudentNames());
console.log('Average age:', getAverageAge());
console.log('Grade A students:', getStudentsByGrade('A'));

